using System;
using System.Collections.Generic;
using System.Linq;
using Newtonsoft.Json;
using NSubstitute;
using NUnit.Framework;
using PriceMonitoring.Inerfaces.Repository;
using PriceMonitoring.Models.DataModel;
using PriceMonitoring.ViewModels;

namespace Tests.Services
{
    [TestFixture]
    public class ProductViewModelTests
    {
        private readonly IImageRepository _imageRepository = Substitute.For<IImageRepository>();
        private readonly IPriceRepository _priceRepository = Substitute.For<IPriceRepository>();
        private ProductViewModel _viewModel;
        private const string _base64Image = "";

        [Test]
        public void ProductViewModel_LoadingAllData()
        {
            //arrange
            var product = new Product{Id = 1, Title = "Product title"};
            var image = new Image{Id = 1, ProductId = product.Id, ImageSource = Convert.FromBase64String(_base64Image) };
            _imageRepository.FindSet(x => x.ProductId == product.Id).ReturnsForAnyArgs(new List<Image>{image});

            var prices = new List<Price> {
                new Price {Id = 1, ProductId = product.Id, RegistrationDate = DateTime.Now.AddDays(-2), ProductPrice = 223},
                new Price {Id = 2, ProductId = product.Id, RegistrationDate = DateTime.Now, ProductPrice = 198},
            };
            _priceRepository.FindSet(x => x.ProductId == product.Id).ReturnsForAnyArgs(prices);

            //act
            _viewModel = new ProductViewModel(_imageRepository, _priceRepository, product);

            //assert
            Assert.AreEqual($"data:image/png;base64,{_base64Image}", _viewModel.Images.FirstOrDefault());
            Assert.AreEqual(getJsonPricesView(prices), _viewModel.Prices);
        }

        [Test]
        public void ProductViewModel_AddingExtraPriceRecordForToday()
        {
            //arrange
            var product = new Product{Id = 1, Title = "Product title"};
            var image = new Image{Id = 1, ProductId = product.Id, ImageSource = Convert.FromBase64String(_base64Image) };
            _imageRepository.FindSet(x => x.ProductId == product.Id).ReturnsForAnyArgs(new List<Image>{image});

            var prices = new List<Price> {
                new Price {Id = 1, ProductId = product.Id, RegistrationDate = DateTime.Now.AddDays(-2), ProductPrice = 223},
                new Price {Id = 2, ProductId = product.Id, RegistrationDate = DateTime.Now.AddDays(-1), ProductPrice = 198},
            };
            _priceRepository.FindSet(x => x.ProductId == product.Id).ReturnsForAnyArgs(prices);

            //act
            _viewModel = new ProductViewModel(_imageRepository, _priceRepository, product);

            prices.Add(new Price { Id = 3, ProductId = product.Id, RegistrationDate = DateTime.Today, ProductPrice = 198});

            //assert
            Assert.AreEqual($"data:image/png;base64,{_base64Image}", _viewModel.Images.FirstOrDefault());
            Assert.AreEqual(getJsonPricesView(prices), _viewModel.Prices);
        }


        private string getJsonPricesView(List<Price> prices)
        {
            var chartModels = new List<ProductViewModel.ChartModel>();
            foreach (var productPrice in prices)
            {
                chartModels.Add(new ProductViewModel.ChartModel
                {
                    X = productPrice.RegistrationDate.Subtract(new DateTime(1970, 1, 1)).TotalMilliseconds,
                    Y = (double)productPrice.ProductPrice
                });
            }

            return JsonConvert.SerializeObject(chartModels);
        }
    }
}
